Component({
  props: {
    className: '',
    show: false,
    title: '',
    // 标题
    leftButtonCls: '',
    // 左按钮样式
    leftButtonText: '',
    // 左按钮标题
    rightButtonCls: '',
    // 右按钮样式
    rightButtonText: '',
    // 右按钮标题
    reverse: false
  },
  methods: {
    onLeftButtonClick: function onLeftButtonClick() {
      var onLeftButtonClick = this.props.onLeftButtonClick;

      if (onLeftButtonClick) {
        onLeftButtonClick();
      }
    },
    onRightButtonClick: function onRightButtonClick() {
      var onRightButtonClick = this.props.onRightButtonClick;

      if (onRightButtonClick) {
        onRightButtonClick();
      }
    }
  }
});