Component({
  mixins: [],
  data: {
    displayValue: ''
  },
  props: {
    min: 0,
    max: 100,
    step: 1,
    disabled: false,
    value: 0,
    showValue: false,
    activeColor: '#0082FF',
    backgroundColor: '#585E65',
    trackSize: '6',
    handleSize: '20',
    handleColor: '#C3C3C3'
  },
  didMount: function didMount() {
    this.setData({
      displayValue: this.props.value
    });
  },
  didUpdate: function didUpdate() {},
  didUnmount: function didUnmount() {},
  methods: {
    onChange: function onChange(e) {
      this.setData({
        displayValue: e.detail.value
      });
      var onChange = this.props.onChange;

      if (onChange) {
        onChange(e);
      }
    },
    onChanging: function onChanging(e) {
      var onChanging = this.props.onChanging;

      if (onChanging) {
        onChanging(e);
      }
    }
  }
});